SELECT * FROM inimigo WHERE (tipo = 'Zumbi' OR tipo = 'Cordyceps') AND habilidades != 'Nenhuma';
SELECT * FROM inimigo WHERE nome_inimigo LIKE '%Zumbi%';

SELECT nome_inimigo, tipo FROM inimigo WHERE tipo = 'Zumbi'; -- Retorna o nome e tipo dos inimigos do tipo "Zumbi"
SELECT nome_inimigo FROM inimigo WHERE fraquezas LIKE '%fogo%'; -- Retorna o nome dos inimigos que possuem a fraqueza "fogo" em sua descrição.
SELECT * FROM inimigo WHERE tipo IN ('Humano', 'Zumbi');

SELECT arma.nome_arma, arma.municao FROM arma
INNER JOIN jogador_personagem_arma ON arma.id_arma = jogador_personagem_arma.id_arma
INNER JOIN personagem ON jogador_personagem_arma.id_personagem = personagem.id_personagem
INNER JOIN inimigo ON personagem.id_personagem = inimigo.id_personagem
WHERE inimigo.tipo = 'Zumbi';
